function resizeSeo(){
    var $ = jQuery ;
    var abs = $('.seo-abs') ;
    if( abs.length ){
        height = abs.height()+50;
        width = $('.seo-abs').width();
        pos = $('.seo-fix').position();
        $('.seo-fix').height(height);

        $('.seo-abs').css({top:pos.top });
    }
}
	
jQuery(document).ready(function($){
    resizeSeo();
});	
jQuery(window).load(function($){
    resizeSeo();
});	
jQuery(window).resize(function($){
    resizeSeo();
});